parameters.jsCode:
const data = $json.body?.data;

return [
  {
    json: {
      reference_id: data?.reference_id || null
    }
  }
];

// يلتقط body سواء كان array أو object
const root = Array.isArray($json.body) ? $json.body[0] : ($json.body ?? $json);
const data = root?.data ?? {};

const order_id  = data.id ?? null;               // لتحديث الحالة لاحقًا (اختياري)
const order_ref = data.reference_id ?? order_id; // رقم الطلب الذي سيدخل الشيت

const cust  = data.customer ?? {};
const phone = String((cust.mobile_code ?? '') + (cust.mobile ?? ''))
  .replace(/[^\d]/g,'')
  .replace(/^0+/,'');

const items = data.items ?? root.items ?? [];

const clean = s => (s ?? '').toString().replace(/\s+/g,' ').trim();

const isXbox = (name='') => /(اكس\s*بوكس|xbox)/i.test(name);
const nameHasCopy = (name='') => /(نسخ|copy)/i.test(name);

// تطابق الخيار المختار مع "حساب نسخ"
const optionIsCopy = (it={}) =>
  Array.isArray(it?.options) && it.options.some(o =>
    String(o?.value?.name ?? '').trim() === 'حساب نسخ (الخيار الاوفر)'
  );

// فلترة عناصر Xbox + حساب نسخ
const xboxCopy = items.filter(i => isXbox(i?.name ?? '') && (nameHasCopy(i?.name ?? '') || optionIsCopy(i)));

const out = [];
for (const it of xboxCopy) {
  const qty  = Math.max(1, Number(it?.quantity ?? 1));
  const name = clean(it?.name ?? '');
  const game = clean(name.split('|')[0]);

  const delivery_option =
    (it?.options ?? [])
      .map(o => o?.value?.name ?? o?.name ?? '')
      .map(clean)
      .find(v => v) || '';

  // كرّر العناصر حسب الكمية – بدون qty و idx
  for (let i = 0; i < qty; i++) {
    out.push({
      json: {
        order_id,
        order_ref,
        customer_phone: phone,
        game,
        product_name: name,
        delivery_option
      }
    });
  }
}

return out.length ? out : [{ json: { note: 'no xbox-copy items' }}];

// ===== إعدادات الشيت =====
const PREV_CODE_NODE = "Code";
const COL_TITLE      = "العاب اكس بوكس";
const COL_HOME       = "Home";
const COL_LOGIN      = "Login";

const COL_LETTER = {
  [COL_TITLE]: "A",
  [COL_HOME]:  "D",
  [COL_LOGIN]: "E",
};

// ===== دوال مساعدة =====
const clean = (s) => (s ?? "")
  .toString()
  .replace(/\s+/g, " ")
  .replace(/\u200f|\u200e|\ufeff/g, "")
  .trim();

// صف حساب (مثل "Fc 26.3"): يحتوي نمط رقم.رقم
const isAccountRowLabel = (s="") => /\d+\.\d+/.test(s);

// ===== 1) سياق من Code node السابق =====
const prevItems = $items(PREV_CODE_NODE, 0);
if (!prevItems.length) return [{ json: { found:false } }];

const ctx = prevItems[0].json;
const baseTitle      = clean(ctx.game || ctx.base_title || "");
const order_id       = ctx.order_id ?? null;
const order_ref      = ctx.order_ref ?? order_id;
const customer_phone = ctx.customer_phone ?? "";
const product_name   = clean(ctx.product_name || "");
const delivery_option= clean(ctx.delivery_option || "");

// ===== 2) صفوف الشيت =====
const rows = $input.all().map(i => i.json);
if (!rows.length) return [{ json: { found:false } }];

// ===== 3) ابحث عن صف العنوان =====
let titleIdx = -1;
for (let i = 0; i < rows.length; i++) {
  const cell = clean(rows[i][COL_TITLE]);
  if (!cell) continue;
  if (cell.includes(baseTitle) || baseTitle.includes(cell)) {
    titleIdx = i; break;
  }
}
if (titleIdx < 0) return [{ json: { found:false } }];

// ===== 4) حدود البلوك تحت العنوان =====
let start = titleIdx + 1;
let end = rows.length - 1;
for (let i = start; i < rows.length; i++) {
  const val = clean(rows[i][COL_TITLE]);
  if (val && !isAccountRowLabel(val)) { end = i - 1; break; }
}
if (start > end) return [{ json: { found:false } }];

// ===== 5) اختر أول صف متاح: Home ثم Login =====
let chosenRow = -1;
let chosenSlot = null;

for (let r = start; r <= end; r++) {
  const label = clean(rows[r][COL_TITLE]);
  if (!label || !isAccountRowLabel(label)) continue;

  const homeVal  = clean(rows[r][COL_HOME]);
  const loginVal = clean(rows[r][COL_LOGIN]);

  if (!homeVal)  { chosenRow = r; chosenSlot = COL_HOME;  break; }
  if (!loginVal) { chosenRow = r; chosenSlot = COL_LOGIN; break; }
}

if (chosenRow < 0) return [{ json: { found:false } }];

// ===== 6) الخرج =====
const picked = rows[chosenRow];
const account_label = clean(picked[COL_TITLE]);

const slot_row = picked.row_number;           // من Get rows
const slot_col = COL_LETTER[chosenSlot];      // "D" لو Home، "E" لو Login

return [{
  json: {
    found: true,

    // سياق الطلب
    order_id,
    order_ref,
    customer_phone,
    product_name,
    base_title: baseTitle,
    delivery_option,

    // الحساب المختار
    account_label,      // مثل "Fc 26.3" أو "BattleField 6.1"
    slot_type: chosenSlot, // "Home" أو "Login"
    slot_row,
    slot_col
  }
}];

