parameters.jsCode:
const SHEET_NODE_NAME = "Get row(s) in sheet";

const COLS = {
  CARD_M: "نيترو قيمنق بطاقة شهر",
  CARD_M_REMAIN: "الحد المتبقي شهر",
  CARD_Y: "نيترو قيمنق بطاقة سنة",
  CARD_Y_REMAIN: "الحد المتبقي سنة",

  GIFT_M: "نيترو قيمنق قيفت شهر",
  GIFT_M_HOURS: "عدد الساعات شهر",
  GIFT_M_LEFT: "الساعات المتبقية شهر",
  GIFT_M_STATUS: "حالة الرابط شهر",
  GIFT_M_NUM: "رقم نيترو شهر",

  GIFT_Y: "نيترو قيمنق قيفت سنة",
  GIFT_Y_HOURS: "عدد الساعات سنة",
  GIFT_Y_LEFT: "الساعات المتبقية سنة",
  GIFT_Y_STATUS: "حالة الرابط سنة",
  GIFT_Y_NUM: "رقم نيترو سنة",

  PROMO: "نيترو قيمنق قيفت 3 اشهر",
  PROMO_STATUS: "حالة الرابط برومو",
  PROMO_EXP: "تاريخ الانتهاء",
  PROMO_NUM: "رقم نيترو 3 اشهر",
};

// أدوات مساعدة
function n(v,d=0){const x=Number(String(v??"").replace(/[^\d.-]/g,""));return Number.isFinite(x)?x:d;}
function norm(s){return String(s??"").trim().toLowerCase();}
function isAvail(val){
  const s=norm(val);
  if(!s) return false;
  if(s.includes("متوفر")||s.includes("متاح")||s.includes("available")||s==="ok"||s==="true"||s==="yes") return true;
  if(s.includes("منتهي")||s.includes("انتهى")||s.includes("expired")||s.includes("غير متاح")||s.includes("not available")||s==="false"||s==="no") return false;
  return false;
}

// هل الاسم يخص منتجات النيترو فقط
function isNitroName(name){
  const s=norm(name);
  return s.includes("نيترو") || s.includes("nitro");
}

// استخراج النوع/الفئة من اسم المنتج نفسه
function parseProductFromName(name){
  const s=norm(name);
  if(!isNitroName(s)) return {productType:null, productCategory:null};

  // نوع المنتج
  let productType=null;
  if(s.includes("برومو")||s.includes("promo")||s.includes("3 اشهر")||s.includes("ثلاث")) productType="promo";
  else if(s.includes("قيفت")||s.includes("gift")) productType="gift";
  else if(s.includes("بطاقة")||s.includes("card")) productType="card";

  // فئة المدة
  let productCategory=null;
  if(s.includes("سنة")||s.includes("year")) productCategory="year";
  else if(s.includes("شهر")||s.includes("month")) productCategory="month";
  else if(productType==="promo") productCategory=null; // برومو ثابت 3 أشهر

  return {productType, productCategory};
}

// لتحديد هل الاسم فيه تلميح مدة/فئة
function nameHasCategory(name){
  const s=norm(name);
  return (
    s.includes("شهر") ||
    s.includes("سنة") ||
    s.includes("month") ||
    s.includes("year") ||
    s.includes("3 اشهر") ||
    s.includes("ثلاث") ||
    s.includes("trial") ||
    s.includes("تجرب") ||
    s.includes("ترايل") ||
    s.includes("promo") ||
    s.includes("برومو")
  );
}

// تجميع سياق الطلب مع الدمج والتصفية على النيترو فقط
function collectCtxDedup(all){
  const map=new Map();
  for(const it of all){
    const j=it.json||{};
    const productName=j.productName??"";
    // تجاهل أي عنصر لا يحتوي على "نيترو"
    if(!isNitroName(productName)) continue;

    const key=[j.productType||"", j.productCategory||"", norm(productName)].join("|");
    const q=Math.max(1,n(j.quantity,1));
    if(!map.has(key)){
      const parsed=parseProductFromName(productName);
      map.set(key,{
        order_id:j.order_id??null,
        order_ref:j.order_ref??null,
        phoneIntl:j.phoneIntl??null,
        productName:productName,
        // إذا كانت حقول النوع/الفئة فارغة، نعتمد على الاسم
        productType:j.productType||parsed.productType||null,
        productCategory:j.productCategory||parsed.productCategory||null,
        quantity:q,
        isNitro:true
      });
    }else{
      const cur=map.get(key);
      if(q>cur.quantity) cur.quantity=q;
    }
  }

  const arr=[...map.values()];
  // إذا لم توجد منتجات نيترو في الطلب كله، لا نخمن من الشيت ولا نرسل أي برومو
  return arr.length?arr:[];
}

const sheetItems=$items(SHEET_NODE_NAME)||[];
const rows=sheetItems.map(it=>it.json||{});
const inputItems=$input.all();
const ctxList=collectCtxDedup(inputItems);

// لا تحاول الاستنتاج من الشيت إذا لم يكن المنتج نيترو
function inferFromRow(row){
  if(row[COLS.PROMO]&&isAvail(row[COLS.PROMO_STATUS])) return {productType:"promo",productCategory:null};
  const giftMonthOk=row[COLS.GIFT_M]&&isAvail(row[COLS.GIFT_M_STATUS]);
  const giftYearOk =row[COLS.GIFT_Y]&&isAvail(row[COLS.GIFT_Y_STATUS]);
  if(giftMonthOk||giftYearOk) return {productType:"gift",productCategory:giftMonthOk?"month":"year"};
  const cardMonthOk=row[COLS.CARD_M]&&n(row[COLS.CARD_M_REMAIN],0)>0;
  const cardYearOk =row[COLS.CARD_Y]&&n(row[COLS.CARD_Y_REMAIN],0)>0;
  if(cardMonthOk||cardYearOk) return {productType:"card",productCategory:cardMonthOk?"month":"year"};
  return {productType:"unknown",productCategory:null};
}

function processOneCtx(ctx){
  // إذا لسبب ما دخل سياق غير نيترو، تجاهله
  if(!ctx.isNitro) return {productType:"unknown",productCategory:null,productName:ctx.productName||"",itemsCollected:[],picked:0,quantity:ctx.quantity||1,found:false};

  let productType=ctx.productType||null;
  let productCategory=ctx.productCategory||null;
  const quantity=ctx.quantity;

  // لا نستنتج من الشيت إلا إذا المنتج نيترو ومفقود النوع
  if((!productType||productType==="unknown")&&rows.length>0){
    const inf=inferFromRow(rows[0]); 
    productType=inf.productType; 
    productCategory=inf.productCategory;
  }

  let iterRows=rows;

  if(productType==="gift"){
    if(productCategory==="month"){
      iterRows=[...rows].sort((a,b)=>n(a[COLS.GIFT_M_LEFT],1e9)-n(b[COLS.GIFT_M_LEFT],1e9));
    }else if(productCategory==="year"){
      iterRows=[...rows].sort((a,b)=>n(a[COLS.GIFT_Y_LEFT],1e9)-n(b[COLS.GIFT_Y_LEFT],1e9));
    }
  } else if(productType==="card"){
    if(productCategory==="month"){
      iterRows=[...rows].sort((a,b)=>n(a[COLS.CARD_M_REMAIN],1e9)-n(b[COLS.CARD_M_REMAIN],1e9));
    }else if(productCategory==="year"){
      iterRows=[...rows].sort((a,b)=>n(a[COLS.CARD_Y_REMAIN],1e9)-n(b[COLS.CARD_Y_REMAIN],1e9));
    }
  }

  const itemsCollected=[];
  let picked=0;

  for(const row of iterRows){
    if(!productType||productType==="unknown") break;

    if(productType==="promo"){
      const link=row[COLS.PROMO];
      const nitroNum=row[COLS.PROMO_NUM];
      const ok=isAvail(row[COLS.PROMO_STATUS]);
      if(link&&ok){ 
        itemsCollected.push({
          value:String(link).trim(),
          nitroNumber:nitroNum?String(nitroNum).trim():null
        }); 
        picked++; 
        if(picked>=quantity) break; 
      }
    } else if(productType==="gift"){
      const useMonth=productCategory==="month";
      const link=row[useMonth?COLS.GIFT_M:COLS.GIFT_Y];
      const nitroNum=row[useMonth?COLS.GIFT_M_NUM:COLS.GIFT_Y_NUM];
      const status=row[useMonth?COLS.GIFT_M_STATUS:COLS.GIFT_Y_STATUS];
      const leftH=row[useMonth?COLS.GIFT_M_LEFT:COLS.GIFT_Y_LEFT];
      if(link&&isAvail(status)){
        itemsCollected.push({
          value:String(link).trim(),
          nitroNumber:nitroNum?String(nitroNum).trim():null,
          hours:n(leftH,0)
        });
        picked++; 
        if(picked>=quantity) break;
      }
    } else if(productType==="card"){
      const useMonth=productCategory==="month";
      const cardVal=row[useMonth?COLS.CARD_M:COLS.CARD_Y];
      const remain=n(row[useMonth?COLS.CARD_M_REMAIN:COLS.CARD_Y_REMAIN],0);
      if(cardVal&&remain>0){
        const canTake=Math.min(remain, quantity-picked);
        itemsCollected.push({
          value:String(cardVal).trim(),
          remain:remain,
          taken:canTake
        });
        picked += canTake;
        if(picked>=quantity) break;
      }
    }
  }

  const found=(productType==="promo"||productType==="gift"||productType==="card") ? itemsCollected.length>=1 : false;
  return {productType,productCategory,productName:ctx.productName||"",itemsCollected,picked,quantity,found};
}

// إن لم توجد أي عناصر نيترو في الطلب، نرجع نتيجة آمنة بدون أي إرسال
if(ctxList.length===0){
  return [{
    json:{
      found: false,
      multiNitro: false,
      order_id: null,
      order_ref: null,
      phoneIntl: null,
      productName: "لا توجد منتجات نيترو في الطلب",
      productType: null,
      productCategory: null,
      quantity: 0,
      textToSend: "",
      pickedCount: 0,
    }
  }];
}

let blocks=[];
let totalPicked=0;
let anyFound=false;

for(const ctx of ctxList){
  const r=processOneCtx(ctx);
  anyFound = anyFound || r.found;
  totalPicked += r.picked;
  if(!r.found) continue;

  let titleBase = r.productName || (
    r.productType==="card" ? "نيترو قيمنق بطاقة" :
    r.productType==="gift" ? "نيترو قيمنق قيفت" :
    r.productType==="promo" ? "نيترو قيمنق قيفت 3 اشهر" : "منتج"
  );

  if (!nameHasCategory(titleBase)) {
    if (r.productType==="card" || r.productType==="gift") {
      if (r.productCategory==="month") titleBase += " | شهر";
      else if (r.productCategory==="year") titleBase += " | سنة";
    } else if (r.productType==="promo") {
      titleBase += " | ثلاثة اشهر";
    }
  }

  // إبراز مدة الرابط بجانب الرابط نفسه (للقيفت)
  const lines=[];
  for (const it of r.itemsCollected){
    let line = String(it.value).trim();
    if (r.productType === "gift" && it.hours !== undefined && it.hours !== null && !Number.isNaN(Number(it.hours))) {
      const h = n(it.hours, 0);
      if (h > 0) line += ` (مدة الرابط ${h} ساعة)`;
    }
    lines.push(line);
  }

  blocks.push({title:titleBase, lines:lines, items:r.itemsCollected, ctx:r});
}

function labelFor(pt, pc){
  if(pt==="promo") return "نيترو 3 اشهر";
  if(pt==="card" && pc==="month") return "نيترو بطاقة شهر";
  if(pt==="card" && pc==="year")  return "نيترو بطاقة سنة";
  if(pt==="gift" && pc==="month") return "نيترو قيفت شهر";
  if(pt==="gift" && pc==="year")  return "نيترو قيفت سنة";
  return null;
}

// ملصقات فقط للمنتجات النيترو المعالجة
const distinctLabels = new Set();
for (const b of blocks){
  const lbl = labelFor(b.ctx.productType, b.ctx.productCategory);
  if (lbl) distinctLabels.add(lbl);
}
const labelsArr = [...distinctLabels];
const multiNitro = labelsArr.length > 1;
let productNameForSwitch = labelsArr.length === 1 ? labelsArr[0] : (labelsArr.length > 1 ? labelsArr.join("\n") : "غير معروف");

const totalRequested=ctxList.reduce((s,c)=>s+Math.max(1,n(c.quantity,1)),0);
let textToSend=blocks.map(b=>`${b.title}\n${b.lines.join("\n")}`).join("\n\n");
if(totalPicked<totalRequested && anyFound){
  const shortage="(انتهت الكميه سنرسلكم الروابط المتبقيه بأقرب وقت)";
  textToSend=textToSend?`${textToSend}\n${shortage}`:shortage;
}

const firstBlock=blocks[0]||null;

// استخراج البيانات (إن وُجدت عناصر)
let outputItems = [];
if(firstBlock && firstBlock.items.length > 0){
  outputItems = firstBlock.items.map(it => {
    const obj = { value: it.value };
    if(it.nitroNumber !== undefined) obj.nitroNumber = it.nitroNumber;
    if(it.hours !== undefined) obj.remainingHours = it.hours;
    if(it.remain !== undefined) {
      obj.remainingLimit = it.remain;
      obj.quantityTaken = it.taken || 0;
      obj.newRemaining = it.remain - (it.taken || 0);
    }
    return obj;
  });
}

// إنشاء outputs منفصلة لكل item
const outputs = outputItems.map(item => {
  const firstCtxProcessed = firstBlock ? firstBlock.ctx : {productType:null, productCategory:null};
  const output = {
    json: {
      found: anyFound,
      multiNitro: multiNitro,
      order_id: ctxList[0]?.order_id??null,
      order_ref: ctxList[0]?.order_ref??null,
      phoneIntl: ctxList[0]?.phoneIntl??null,
      productName: productNameForSwitch,
      productType: firstCtxProcessed.productType,
      productCategory: firstCtxProcessed.productCategory,
      quantity: totalRequested,
      textToSend: textToSend,
      pickedCount: totalPicked,
    }
  };

  if(firstCtxProcessed.productType === "card"){
    output.json.cardValue = item.value;
    output.json.remainingLimit = item.remainingLimit;
    output.json.quantityTaken = item.quantityTaken;
    output.json.newRemaining = item.newRemaining;
  }
  if(firstCtxProcessed.productType === "gift" || firstCtxProcessed.productType === "promo"){
    output.json.giftLink = item.value;
    output.json.nitroNumber = item.nitroNumber;
    if(item.remainingHours !== undefined){
      output.json.remainingHours = item.remainingHours;
    }
  }
  return output;
});

// إذا لا توجد عناصر نيترو صالحة للإرسال
if(outputs.length === 0){
  return [{
    json:{
      found: false,
      multiNitro: labelsArr.length>1,
      order_id: ctxList[0]?.order_id??null,
      order_ref: ctxList[0]?.order_ref??null,
      phoneIntl: ctxList[0]?.phoneIntl??null,
      productName: productNameForSwitch || "لا توجد عناصر نيترو صالحة",
      productType: null,
      productCategory: null,
      quantity: totalRequested,
      textToSend: "",
      pickedCount: 0,
    }
  }];
}

return outputs;

