parameters.jsCode:
function normalize(str=""){return String(str).trim().toLowerCase();}
function digitsOnly(v){return String(v??"").replace(/[^\d]/g,"").trim();}
function makeFullPhone(cc,m){cc=digitsOnly(cc);m=digitsOnly(m);return cc&&m?cc+m:(m||cc||"");}
function findOrderNode(o){if(!o||typeof o!=="object")return null;const f=Object.prototype.hasOwnProperty.call(o,"reference_id")&&Array.isArray(o.items);if(f)return o;for(const k of Object.keys(o)){const v=o[k];if(v&&typeof v==="object"){const r=findOrderNode(v);if(r)return r;}}return null;}

function classifyProduct(name){
  const s=normalize(name);
  const isGift=/(قيفت|gift)\b/.test(s);
  const isPromo=/(الحسابات الجديدة|تجرب|ترايل|trial|promo|برومو)/.test(s);
  const isCard=/(بطاق|card)\b/.test(s)&&!isGift&&!isPromo;
  let productType="unknown";
  if(isPromo)productType="promo";
  else if(isGift)productType="gift";
  else if(isCard)productType="card";
  let productCategory=null;
  if(productType==="card"||productType==="gift"){
    if(s.includes("شهر")||s.includes("month"))productCategory="month";
    else if(s.includes("سنة")||s.includes("year"))productCategory="year";
  }
  return {productType,productCategory};
}

function get(o,p,d){try{return p.split(".").reduce((a,c)=>a==null?undefined:a[c],o)??d;}catch{return d;}}

const out=[];
let savedCtx=false;

for (const item of items){
  const root=item.json??item;
  const order=findOrderNode(root)||get(root,"body.data",root.data||{});
  let order_id=order?.id??get(root,"body.data.id",get(root,"data.id",null));
  let order_ref=order?.reference_id??get(root,"body.data.reference_id",get(root,"data.reference_id",null));
  const cc=get(order,"customer.mobile_code",get(root,"body.data.customer.mobile_code",""));
  const mob=get(order,"customer.mobile",get(root,"body.data.customer.mobile",""));
  const phoneIntl=makeFullPhone(cc,mob);
  const orderItems=Array.isArray(order?.items)?order.items:[];
  if(orderItems.length===0){
    const maybe=get(root,"items[0]")||root;
    const name=maybe?.name||maybe?.product?.name||"";
    const quantity=Number(maybe?.quantity??1)||1;
    if(name){
      const {productType,productCategory}=classifyProduct(name);
      if(!savedCtx){try{const g=getWorkflowStaticData('global');g.orderCtx={order_id,order_ref,phoneIntl,productName:name,productType,productCategory,quantity};savedCtx=true;}catch{}}
      out.push({json:{order_id,order_ref,phoneIntl,productName:name,quantity,productType,productCategory}});
      continue;
    }
    if(!savedCtx){try{const g=getWorkflowStaticData('global');g.orderCtx={order_id,order_ref,phoneIntl,productName:null,productType:null,productCategory:null,quantity:1};savedCtx=true;}catch{}}
    out.push({json:{order_id,order_ref,phoneIntl}});
    continue;
  }
  for(const line of orderItems){
    const name=line?.name||line?.product?.name||"";
    const quantity=Number(line?.quantity??1)||1;
    const {productType,productCategory}=classifyProduct(name);
    if(!savedCtx){try{const g=getWorkflowStaticData('global');g.orderCtx={order_id,order_ref,phoneIntl,productName:name,productType,productCategory,quantity};savedCtx=true;}catch{}}
    out.push({json:{order_id,order_ref,phoneIntl,productName:name,quantity,productType,productCategory}});
  }
}

return out;

// يختار أول كود غير فاضي ويرتب لك كل القيم للارسال والتحديث
const item = $json;

// خريطة عمود الـPS من حرف العمود
const psMap = { E: 'PS5.1', F: 'PS5.2', G: 'PS4.1', H: 'PS4.2' };
const ps_col_header = psMap[item.slot_col];           // مثل PS4.2
if (!ps_col_header) {
  return { json: { ...item, error: 'invalid_slot_col' } };
}

// أول كود متاح
const codes = Array.isArray(item.code_cells) ? item.code_cells : [];
const first = codes.find(c => c && c.value && String(c.value).trim() !== '');
if (!first) {
  // حسب كلامك دائماً في كود — لو صار العكس نرجّع خطأ واضح
  return { json: { ...item, error: 'no_code_found' } };
}

// رقم الواتساب بصيغة دولية بدون +
const customer_phone = String(item.customer_phone || '').trim();

return {
  json: {
    ...item,
    customer_phone,                 // لارسال الواتساب             
    chosen_code: first.value,  // الكود المختار
    code_col_header: first.header,   // مثل "Code 1"
    code_col_letter: first.col,      // I..R
    ps_col_header,             // مثل PS4.2
    ps_cell_a1: `${item.slot_col}${item.slot_row}`,      // مثال H61
    code_cell_a1: `${first.col}${item.slot_row}`         // مثال I61
  }
};

// ===== helpers =====
const clean = (s) => (s ?? '')
  .toString()
  .replace(/\s+/g, ' ')
  .replace(/\u200f|\u200e/g, '')
  .trim();

const getPlatform = (name='') => {
  if (/سوني\s*5|ps\s*5/i.test(name)) return 'PS5';
  if (/سوني\s*4|ps\s*4/i.test(name)) return 'PS4';
  return null;
};

const getBaseTitle = (name='') => {
  let t = name;
  // احذف الجزء الأخير: "| سوني 4/5"
  t = t.replace(/\|\s*سوني\s*[45]\s*$/i, '');
  // لو فيه أكثر من | خذ الجزء الأول
  t = t.split('|')[0];
  return clean(t);
};

// ===== input normalization =====
// ندعم كل الأشكال: (body[0]) أو (body) أو الجذر نفسه
const root =
  Array.isArray($json.body) ? $json.body[0]
  : ($json.body ?? $json);

const data = root?.data ?? {};

// مهم: نفرّق بين الاثنين
const orderId    = data.id ?? null;                 // تستخدمه لتغيير حالة الطلب
const orderRef   = data.reference_id ?? orderId;    // لرقم الطلب الحقيقي للشيت

// رقم العميل بصيغة دولية (بدون + ورموز)
const cust = data.customer ?? {};
const mobileCode = String(cust.mobile_code ?? '');
const mobile = String(cust.mobile ?? '');
const digits = (mobileCode + mobile).replace(/[^\d]/g, '');
const customer_phone = digits.replace(/^0+/, ''); // شيل أصفار البداية لو فيه

// عناصر سوني فقط
const items = data.items ?? root.items ?? [];
const rawItems = items.filter(i => /سوني|ps\s*[45]/i.test(i?.name ?? ''));

const out = [];

for (const it of rawItems) {
  const qty = Math.max(1, Number(it?.quantity ?? 1));
  const productName = clean(it?.name ?? '');
  const platform = getPlatform(productName);
  const base_title = getBaseTitle(productName);

  for (let k = 0; k < qty; k++) {
