        order_ref: orderRef,      // هذا اللي تحطه في الشيت
        product_name: productName,
        base_title,
        platform,                 // "PS4" أو "PS5"
        customer_phone
      }
    });
  }
}

if (out.length === 0) {
  return [{ json: { playstation_items: 0, note: 'No PlayStation items found' } }];
}

return out;

// ---------- ضبط أسماء الأعمدة ----------
const PREV_CODE_NODE = "Code3";

// عنوان اللعبة قد يكون في أحد هذين العمودين
const TITLE_COLS = ["العاب سوني 4 & 5", "العاب سوني 5"];

const COL_EMAIL = "ايميل الحساب";
const COL_PASS  = "باسورد الحساب";
const CODE_HEADERS = [
  "Code 1","Code 2","Code 3","Code 4","Code 5",
  "Code 6","Code 7","Code 8","Code 9","Code 10"
];
const COL_LETTER = {
  "PS5.1":"E","PS5.2":"F","PS4.1":"G","PS4.2":"H",
  "Code 1":"I","Code 2":"J","Code 3":"K","Code 4":"L","Code 5":"M",
  "Code 6":"N","Code 7":"O","Code 8":"P","Code 9":"Q","Code 10":"R"
};

// تنظيف قوي
function clean(s) {
  if (!s && s !== 0) return '';
  return String(s)
    .replace(/\s+/g, ' ')
    .replace(/\u200f|\u200e|\ufeff/g, '')
    .trim();
}
function isAccountId(a){ return /^[\w.\-]+$/.test(a || ""); }

// 1) سياق من نود Code
const srcItems = $items(PREV_CODE_NODE, 0);
if (!srcItems.length) return [{ json: { found:false, reason:"missing_prev_code_item" } }];
const ctx = srcItems[0].json;

const baseTitle      = clean(ctx.base_title || "");
const platform       = ctx.platform; // PS4 | PS5
const orderId        = ctx.order_id;
const orderRef       = ctx.order_ref ?? null;      // رقم الطلب الحقيقي
const productName    = clean(ctx.product_name || "");
const customer_phone = ctx.customer_phone || "";

// 2) صفوف من الـ Merge
const rows = $input.all().map(i => i.json);
if (!rows.length) return [{ json: { found:false, reason:"no_rows" } }];

// 3) ابحث عن سطر عنوان اللعبة بأي عمود من TITLE_COLS (تطابق جزئي)
let titleIdx = -1;
let titleColUsed = null;

for (let i = 0; i < rows.length; i++) {
  for (const COL_TITLE of TITLE_COLS) {
    const cell = clean(rows[i][COL_TITLE]);
    if (!cell) continue;
    if (cell.includes(baseTitle) || baseTitle.includes(cell)) {
      titleIdx = i;
      titleColUsed = COL_TITLE;
      break;
    }
  }
  if (titleIdx >= 0) break;
}
if (titleIdx < 0) {
  return [{ json: { found:false, reason:"title_not_found", baseTitle } }];
}

// 4) حدود البلوك تحت العنوان حتى العنوان التالي
let start = titleIdx + 1;
let end = rows.length - 1;
for (let i = start; i < rows.length; i++) {
  const a = clean(rows[i][titleColUsed]);
  if (a && !isAccountId(a)) { end = i - 1; break; }
}

// 5) اختَر أول صف بحقل منصة فاضي
const tryCols = platform === "PS5" ? ["PS5.1","PS5.2"] : ["PS4.1","PS4.2"];
let chosenRow = -1;
let chosenHeader = null;

for (let r = start; r <= end; r++) {
  const acc = clean(rows[r][titleColUsed]);
  if (!isAccountId(acc)) continue;
  for (const h of tryCols) {
    const v = clean(rows[r][h]);
    if (!v) { chosenRow = r; chosenHeader = h; break; }
  }
  if (chosenRow >= 0) break;
}
if (chosenRow < 0) {
  return [{ json: { found:false, reason:"no_empty_slot", title: baseTitle } }];
}

// 6) جهّز القيم
const picked = rows[chosenRow];
const accountLabel = clean(picked[titleColUsed]);
const email    = clean(picked[COL_EMAIL]);
const password = clean(picked[COL_PASS]);

const code_cells = CODE_HEADERS.map(h => ({
  header: h,
  col: COL_LETTER[h],
  value: clean(picked[h] || "")
}));

// ✅ تحقق أن فيه على الأقل كود واحد غير فاضي
const hasAnyCode = code_cells.some(c => c.value && String(c.value).trim() !== '');
if (!hasAnyCode) {
  return [{
    json: {
      found: false,
      reason: "no_code_available",
      title: baseTitle,
      sheet_name: titleColUsed
    }
  }];
}

const slot_row = picked.row_number;           // من Get rows
const slot_col = COL_LETTER[chosenHeader];    // حرف العمود E/F/G/H

return [{
  json: {
    found: true,
    sheet_name: titleColUsed,   // يبين من أي شيت جت
    order_id: orderId,
    order_ref: orderRef,
    customer_phone,
    product_name: productName,
    base_title: baseTitle,
    platform,
    account_label: accountLabel,
    slot_row,
    slot_col,
    email,
    password,
    code_cells
  }
}];

