const COLS = (plan_kind === 'day')
  ? { status: 'حالة الحساب يوم', email: 'ايميل حسابات يوم', password: 'باسورد حسابات يوم', end: 'نهاية الاشتراك يوم' }
  : { status: 'حالة الحساب شهر', email: 'ايميل حسابات شهر', password: 'باسورد حسابات شهر', end: 'نهاية الاشتراك شهر' };

