// n8n Code node (JavaScript)

const inputItems = $input.all();

// ==============================
// تنسيق التاريخ إلى MM/DD/YYYY
// ==============================
function formatToMDY(dateStr) {
  if (!dateStr) return null;

  const m = String(dateStr).match(/^(\d{4})-(\d{2})-(\d{2})/);
  if (!m) return null;

  const y = Number(m[1]);
  const mo = Number(m[2]);
  const d = Number(m[3]);
  return `${mo}/${d}/${y}`;
}

// ==========================================
// تنظيف الاسم + تحديد المنصة / القسم
// ==========================================
function normalizeName(fullName) {
  if (!fullName) return { baseName: null, platform: 'other' };

  // الاسم قبل |
  const baseName = fullName.split('|')[0].trim();

  const name = fullName.toLowerCase();
  let platform = 'other';

  // --- 1) نقاط (Valorant / LoL / RP / VP / Points) ---
  if (
    name.includes('نقاط') ||
    name.includes('points') ||
    name.includes('vp') ||
    name.includes('rp') ||
    name.includes('بوينت')
  ) {
    platform = 'points';

  // --- 2) حسابات عشوائية Steam ---
  } else if (
    (name.includes('حساب') || name.includes('account')) &&
    (name.includes('ستيم') || name.includes('steam')) &&
    (name.includes('عشوائي') || name.includes('random'))
  ) {
    platform = 'accounts';

  // --- 3) IPTV ---
  } else if (
    name.includes('نيورت تي في') ||
    name.includes('neurt tv') ||
    name.includes('iptv') ||
    name.includes('tv pro') ||
    (name.includes('اشتراك') && name.includes('تي في'))
  ) {
    platform = 'iptv';

  // --- 4) Steam (ألعاب أونلاين فقط) ---
  } else if (
    (name.includes('ستيم') || name.includes('steam')) &&
    (
      name.includes('اونلاين') ||
      name.includes('online') ||
      name.includes('multiplayer') ||
      name.includes('co-op') ||
      name.includes('coop')
    )
  ) {
    platform = 'steam';

  // --- 5) PS5 ---
  } else if (
    name.includes('سوني5') ||
    name.includes('سوني 5') ||
    name.includes('ps5')
  ) {
    platform = 'ps5';

  // --- 6) PS4 ---
  } else if (
    name.includes('سوني4') ||
    name.includes('سوني 4') ||
    name.includes('ps4')
  ) {
    platform = 'ps4';

  // --- 7) Xbox (أي ظهور لـ xbox / اكس بوكس) ---
  } else if (
    /(\b(x\s*box|xbox)\b|اكس\s*بوكس|إكس\s*بوكس)/i.test(fullName)
  ) {
    platform = 'xbox';
  }

  return { baseName, platform };
}

// ==============================
// المعالجة الرئيسية
// ==============================
const out = [];

for (const item of inputItems) {
  const body = item.json.body || item.json || {};
  const data = body.data || {};

  const referenceId   = data?.reference_id ?? null;
  const paymentMethod = data?.payment_method ?? null;

  const rawDate =
    (data?.date && typeof data.date === 'object'
      ? data?.date?.date
      : data?.date) ?? null;

  const createdAtFormatted = formatToMDY(rawDate);

  const paidAmount =
    data?.payment_actions?.remaining_action?.paid_amount?.amount ??
    data?.payment_actions?.refund_action?.paid_amount?.amount ??
    null;

  // عنصر منفصل لكل منتج
  for (const p of data.items || []) {
    if (!p?.name) continue;

    const fullName = p.name;
    const qty = p.quantity ?? 1;

    const { baseName, platform } = normalizeName(fullName);

    out.push({
      json: {
        reference_id: referenceId,
        payment_method: paymentMethod,
        created_at: createdAtFormatted,
        full_name: fullName,
        base_name: baseName,
        platform,
        qty,
        paid_amount: paidAmount,
      },
    });
  }
}

return out;

const items = $input.all().map(item => {
