const CREDIT_TABLE = {
  "نيورت تي في شهر":            0.5,
  "نيورت تي في 3 اشهر":         1,
  "نيورت تي في 6 اشهر":         2,
  "نيورت تي في سنة":            4,

  "نيورت تي في برو شهر":        1,
  "نيورت تي في برو 6 اشهر":     3,
  "نيورت تي في برو سنة":        5,
  "نيورت تي في برو سنة جهازين": 8,
};

function clean(s) {
  if (!s && s !== 0) return '';
  return String(s)
    .replace(/\s+/g, ' ')
    .replace(/\u200f|\u200e|\ufeff/g, '')
    .trim();
}

// ===== 1) صف الشيت القادم من الـ Set node =====
const row = $input.item.json;

// ===== 2) قراءة qty من نود Switch =====
let qty = 1;
try {
  const switchItem = $items('Switch9', 0, 0);
  if (switchItem && switchItem.json && switchItem.json.qty != null) {
    qty = Number(switchItem.json.qty);
  }
} catch (e) {
  qty = 1;
}

// ===== 3) تحديد قيمة الخصم من full_name =====
const fullName = clean(row.full_name || '');

let isPro = fullName.includes('برو');
let duration = '';

if (fullName.includes('سنة جهازين')) {
  duration = 'سنة جهازين';
} else if (fullName.includes('سنة')) {
  duration = 'سنة';
} else if (fullName.includes('6 اشهر') || fullName.includes('6 أشهر')) {
  duration = '6 اشهر';
} else if (fullName.includes('3 اشهر') || fullName.includes('3 أشهر')) {
  duration = '3 اشهر';
} else if (fullName.includes('شهر')) {
  duration = 'شهر';
}

let creditKey = '';
if (duration) {
  creditKey = (isPro ? 'نيورت تي في برو' : 'نيورت تي في') + ' ' + duration;
}

const creditAmount = CREDIT_TABLE[creditKey] ?? 0;

// ===== 4) حساب المخزون الجديد =====
const oldStock = Number(row['مخزون ابتدائي 5'] ?? 0);

const newStock = oldStock - creditAmount * qty; // يسمح بالقيمة السالبة

// ===== 5) إعادة النتيجة =====
return [
  {
    json: {
      'مشاهدة': row['مشاهدة'],
      qty,
      oldStock,
      newStock,
      'مخزون ابتدائي 5': newStock,
