];

const items = $input.all().map(item => {
  const row = item.json;

  const qty = Number(row.qty ?? 1);

  // غيّر اسم العمود حسب شيتك
  const oldStock = Number(row['مخزون ابتدائي (الحسابات)'] ?? 0);

  const newStock = oldStock - qty;

  return {
    json: {
      ...row,
      qty,
      oldStock,
      newStock,
    },
