});

return items;

const STOCK_TITLE_COL = "اكس بوكس نسخ";
const STATUS4_COL = "حالة المنتج (فعال / موقوف) 4";
const STOCK4_COL  = "مخزون للمتاحة 4";

const XBOX_TITLE_COL = "العاب اكس بوكس";
const EMAIL_COL = "الايميل";
const PASS_COL  = "الباسورد";
const HOME_COL  = "Home";
const LOGIN_COL = "Login";

function clean(s) {
  if (s === null || s === undefined) return "";
  return String(s).replace(/[\u200f\u200e\ufeff]/g, "").trim();
}

function keyOfTitle(s) {
  return clean(s)
    .toLowerCase()
    .replace(/[إأآا]/g, "ا")
    .replace(/ى/g, "ي")
    .replace(/ة/g, "ه")
    .replace(/[\|\-–—_:؛،,.!؟'"()\[\]{}\\/]/g, " ")
    .replace(/\s+/g, " ")
    .trim();
}

function isEmptyLike(v) {
  const x = clean(v).replace(/\s+/g, " ").trim();
  return x === "" || x === "-" || x === "—" || x === "0";
}

function score(home, login) {
  const hEmpty = isEmptyLike(home);
  const lEmpty = isEmptyLike(login);
  if (hEmpty && lEmpty) return 2;
  if (hEmpty || lEmpty) return 1;
  return 0;
}

// اقرأ مباشرة من النودات
const stockRows = $items("Read_Stock_XboxTitles").map(i => i.json);
const rows      = $items("Xbox1").map(i => i.json);

// تشخيص
if (!stockRows.length || !rows.length) {
  return [{ json: { stockRows: stockRows.length, xboxRows: rows.length } }];
}

// Xbox aggregation
const totalsByKey = new Map();
const hasCredsByKey = new Map();
const foundKeys = new Set();

let currentKey = null;

for (const r of rows) {
  const titleRaw = clean(r[XBOX_TITLE_COL]);
  const email = clean(r[EMAIL_COL]);
  const pass  = clean(r[PASS_COL]);

  const isTitleRow = !!titleRaw && !email && !pass;

  if (isTitleRow) {
    currentKey = keyOfTitle(titleRaw);
    foundKeys.add(currentKey);
    if (!totalsByKey.has(currentKey)) totalsByKey.set(currentKey, 0);
    if (!hasCredsByKey.has(currentKey)) hasCredsByKey.set(currentKey, false);
    continue;
  }

  if (!currentKey) continue;
  if (!email || !pass) continue;

  hasCredsByKey.set(currentKey, true);
  const s = score(r[HOME_COL], r[LOGIN_COL]);
  totalsByKey.set(currentKey, (totalsByKey.get(currentKey) || 0) + s);
}

// Stock output
const out = [];

for (const sRow of stockRows) {
  const game_title = clean(sRow[STOCK_TITLE_COL]);
  if (!game_title) continue;

  const k = keyOfTitle(game_title);

  const titleExists = foundKeys.has(k);
  const credsExist  = hasCredsByKey.get(k) === true;

  const status = (titleExists && credsExist) ? "فعال" : "موقوف";
  const available_units = (titleExists && credsExist) ? (totalsByKey.get(k) || 0) : 0;

  out.push({
    json: {
      game_title,
      available_units,
      [STOCK4_COL]: available_units,
      [STATUS4_COL]: status,
    }
  });
}

return out;

// ---------- ضبط أسماء الأعمدة ----------
const TITLE_COLS = ["العاب سوني 4 & 5", "العاب سوني 5"];

// تنظيف قوي
function clean(s) {
  if (!s && s !== 0) return '';
  return String(s)
    .replace(/\s+/g, ' ')
    .replace(/\u200f|\u200e|\ufeff/g, '')
    .trim();
}

// نعتبر الـ Account ID شيء مثل email/username بدون مسافات
function isAccountId(a){ 
  return /^[\w.\-]+$/.test(a || ""); 
}

// 1) جلب الصفوف من الـ input (من نود الـ Merge)
const rows = $input.all().map(i => i.json);
if (!rows.length) {
  return [];
}

const results = [];

for (let i = 0; i < rows.length; i++) {
  let titleColUsed = null;
  let gameTitle = "";

  // هل هذا السطر هو عنوان لعبة؟
  for (const col of TITLE_COLS) {
    const cell = clean(rows[i][col]);
    if (!cell) continue;
    if (!isAccountId(cell)) {
      titleColUsed = col;
      gameTitle = cell;
      break;
    }
  }

  if (!titleColUsed) continue;

  // حدود البلوك
  let start = i + 1;
  let end = rows.length - 1;

  for (let j = start; j < rows.length; j++) {
    let isNewTitle = false;

    for (const col of TITLE_COLS) {
      const c = clean(rows[j][col]);
      if (c && !isAccountId(c)) {
        isNewTitle = true;
        break;
      }
    }

    if (isNewTitle) {
      end = j - 1;
      break;
    }
  }

  // حساب المخزون
  let emptyPS5 = 0;
  let emptyPS4 = 0;

  for (let r = start; r <= end; r++) {
    const acc = clean(rows[r][titleColUsed]);
    if (!isAccountId(acc)) continue;

    // الشرط الجديد: يجب أن يكون فيه ايميل وباسورد
    const email = clean(rows[r]["ايميل الحساب"]);
    const pass  = clean(rows[r]["باسورد الحساب"]);
    if (!email || !pass) continue;   // تجاهل السطر بالكامل

    // خانات PS5
    const ps5_1 = clean(rows[r]["PS5.1"]);
    const ps5_2 = clean(rows[r]["PS5.2"]);
    if (!ps5_1) emptyPS5++;
    if (!ps5_2) emptyPS5++;

    // خانات PS4
    const ps4_1 = clean(rows[r]["PS4.1"]);
    const ps4_2 = clean(rows[r]["PS4.2"]);
    if (!ps4_1) emptyPS4++;
    if (!ps4_2) emptyPS4++;
  }

  results.push({
    json: {
      game_title: gameTitle,
      title_column: titleColUsed,
      empty_ps5_slots: emptyPS5,
      empty_ps4_slots: emptyPS4
    }
  });

  i = end;
}

return results;

// n8n Code node (JavaScript)

const inputItems = $input.all();

function formatToMDY(dateStr) {
  if (!dateStr) return null;

  // نمط yyyy-mm-dd ... (نتجاهل الوقت والميكروثواني)
  const m = String(dateStr).match(/^(\d{4})-(\d{2})-(\d{2})/);
  if (!m) return null;

  const y = Number(m[1]);
  const mo = Number(m[2]); // 1..12
  const d = Number(m[3]);  // 1..31
  // بدون أصفار بادئة
  return `${mo}/${d}/${y}`;
}

return inputItems.map((item) => {
  const body = item.json.body || item.json || {};
  const data = body.data || {};

  const products = (data.items || [])
    .map((i) => i?.name)
    .filter(Boolean)
    .join('\n');

  const paidAmount =
    data?.payment_actions?.remaining_action?.paid_amount?.amount ??
    data?.payment_actions?.refund_action?.paid_amount?.amount ??
    null;

  // ناخذ التاريخ من data.date.date أولاً، ثم نجرّب data.date إن كان نص
  const rawDate =
    (data?.date && typeof data.date === 'object' ? data?.date?.date : data?.date) ??
    null;

  const createdAtFormatted = formatToMDY(rawDate); // مثال: 11/1/2025

  return {
    json: {
      reference_id: data?.reference_id ?? null,
      payment_method: data?.payment_method ?? null,
      created_at: createdAtFormatted, // 11/1/2025
      products,
      paid_amount: paidAmount
    }
  };
});

// الحصول على اسم المنتج من البيانات الواردة
// قراءة من webhook -> body -> data -> items[0] -> product -> name
const productName = $input.item.json.body?.data?.items?.[0]?.product?.name || 
                    $input.item.json.product?.name || 
                    $input.item.json.name || 
                    $input.item.json.productName || 
                    '';

// تنظيف النص وإزالة المسافات الزائدة
const productLower = productName.toLowerCase().trim();

let orderType = 'UNKNOWN';

// التحقق من نوع المنتج بناءً على الكلمات المفتاحية
// ترتيب الشروط من الأطول للأقصر لتجنب التطابقات الخاطئة
if (productLower.includes('ستيم اوفلاين') || productLower.includes('steam offline')) {
  orderType = 'STEAM';
} 
else if (productLower.includes('نيترو قيمنق') || productLower.includes('nitro gaming')) {
  orderType = 'DISCORD';
} 
else if (productLower.includes('شات جي بي تي') || productLower.includes('chat gpt')) {
  orderType = 'CHATGPT';
}
else if (productLower.includes('اكس بوكس') || productLower.includes('xbox')) {
  orderType = 'XBOX';
} 
else if (productLower.includes('سوني') || productLower.includes('sony') || productLower.includes('playstation') || productLower.includes('ps4') || productLower.includes('ps5')) {
  orderType = 'PS';
}

// إضافة حقول ثابتة

// إرجاع النتيجة مع جميع البيانات الأصلية
return {
  ...($input.item.json),
  orderType: orderType,
  productName: productName
};

const MAX_MS = 49_000;
const MAX_ATTEMPTS = 7;

const elapsed      = Date.now() - Number($json.startTs);
const loopContinue = elapsed < MAX_MS && Number($json.attempt) < MAX_ATTEMPTS;

return [{
  json: {
    ...$json,                 // يبقي userId و غيره
    attempt: $json.attempt + 1,
    loopContinue
  }
}];

const orderNumber = Number($('Webhook').first().json.query.orderNumber);
const inputData = $input.all();

if (!inputData || inputData.length === 0) {
  return [];
}

const results = [];

for (const item of inputData) {
  const row = item.json;
  let statusName = null;
  let limit = null;

  if (row && typeof row === 'object') {
    if (row.B !== undefined) {
      statusName = row.B;
    }
    else if (row.status_name || row.status) {
      statusName = row.status_name || row.status;
    }
    else {
      const keys = Object.keys(row);
      if (keys.length > 1) {
        statusName = row[keys[1]];
      }
    }

    // استخراج الحد المتبقي
    if (row['الحد المتبقي'] !== undefined) {
      limit = row['الحد المتبقي'];
    } else if (row.limit !== undefined) {
      limit = row.limit;
    } else if (row.C !== undefined) {
      limit = row.C;
    }
  }

  // تحويل status_name إلى رقم إذا كانت قيمة صالحة
  const statusNumber = statusName !== null && !isNaN(statusName)
    ? Number(statusName)
    : null;

  results.push({
    json: {
      status_name: statusNumber,  // الآن رقم بدل string
      orderNumber: orderNumber,
      limit: limit
    }
  });
}

return results;

// فلترة الآيتيمات اللي ما فيها بيانات (empty objects)
const validItems = items.filter(item => Object.keys(item.json).length > 0);

if (validItems.length === 0) {
  // مافيه أي نتيجة أو كلها فاضية
  return [{ json: { noResult: true } }];
} else {
  // فيه نتيجة صحيحة
  return validItems.map(item => ({
    json: {
      ...item.json,
      noResult: false
    }
  }));
}

// في Parse Mail - أضف steamUsername و userId للـ base
