const userIdRaw = webhook.query?.userId ?? webhook.body?.userId ?? base?.userId ?? '';
const userId = String(userIdRaw || '').toLowerCase().trim();

function parseSnippet(snippet = '', internalDate = 0) {
  const userMatch  = snippet.match(/Steam\s+([^\s,]+),/i);
  const username   = userMatch ? userMatch[1] : null;

  const upper = String(snippet || '').toUpperCase();
  let code = null;

  const guardPhrase = 'STEAM GUARD CODE';
  const footerMarkers = [
    "IF THIS WASN'T YOU",
    "IF THIS WASN’T YOU",
    "IF YOU DIDN'T REQUEST THIS",
    "IF YOU DIDN’T REQUEST THIS"
  ];

  const unameUpper = username ? String(username).toUpperCase() : null;
  const tokensRegex = /\b[A-Z0-9]{5}\b/g;

  const findBeforeFooter = (text, startIdx = 0) => {
    let end = text.length;
    for (const m of footerMarkers) {
      const idx = text.indexOf(m, startIdx);
      if (idx !== -1 && idx < end) end = idx;
    }
    const win = text.slice(startIdx, end);
    const cands = win.match(tokensRegex) || [];
    const filtered = cands.filter(c => c !== unameUpper);
    return filtered.length ? filtered[filtered.length - 1] : null;
  };

  const guardIdx = upper.indexOf(guardPhrase);
  if (guardIdx !== -1) {
    const start = guardIdx + guardPhrase.length;
    code = findBeforeFooter(upper, start);
  }

  if (!code) code = findBeforeFooter(upper, 0);

  if (!code) {
    const all = upper.match(tokensRegex) || [];
    const filtered = all.filter(c => c !== unameUpper);
    if (filtered.length) code = filtered[filtered.length - 1];
  }

  const isEmailExpired = Date.now() - Number(internalDate) > 300000;
  return { username, steamCode: code, isEmailExpired };
}

return $input.all().map(({ json }, i) => ({
  json: { 
    ...base,
    steamUsername, // lowercase
    userId,        // lowercase (إذا وُجد)
    ...parseSnippet(json.snippet, json.internalDate)
  },
  pairedItem: i
}));

// فلترة الآيتيمات اللي ما فيها بيانات (empty objects)
const validItems = items.filter(item => Object.keys(item.json).length > 0);

if (validItems.length === 0) {
  // مافيه أي نتيجة أو كلها فاضية
  return [{ json: { noResult: true } }];
} else {
  // فيه نتيجة صحيحة
  return validItems.map(item => ({
    json: {
      ...item.json,
      noResult: false
    }
  }));
}

// أسماء الأعمدة في الشيت
const COL_ORDER   = "رقم الطلب";
const COL_EMAIL   = "ايميل الحساب";
const COL_PASS    = "باسورد الحساب";
const COL_STATUS  = "حالة الاشتراك";
const COL_LIMIT   = "الحد المتبقي";   // الاسم الشائع
const COL_LIMIT_2 = "# الحد المتبقي"; // في حال كان العمود يبدأ بـ #

/* اقرأ orderNumber من الويب هوك (query أو body) كنص */
const webhookJson = ($items('ChatgptVerification', 0, 0) || [])[0]?.json || {};
const orderNumber = String(
  webhookJson.query?.orderNumber ??
  webhookJson.body?.orderNumber ??
  ""
).trim();

const rows = $input.all();
const out = [];

for (const item of rows) {
  const row = item.json;

  const rowOrder  = String(row[COL_ORDER]  ?? "").trim();
  const rowEmail  = String(row[COL_EMAIL]  ?? "").trim();
  const rowPass   = String(row[COL_PASS]   ?? "").trim();
  const rowStatus = String(row[COL_STATUS] ?? "").trim();

  // قراءة "الحد المتبقي" كما هو من الشيت (دعم الاسمين)
  // إن كان خاليًا نرجّعه null بدل 0 لتفريق "لا توجد قيمة" عن الصفر الحقيقي
  const rawLimit = row[COL_LIMIT] ?? row[COL_LIMIT_2];
  const limit = (rawLimit === undefined || rawLimit === null || rawLimit === "")
    ? null
    : Number(rawLimit);

  out.push({
    json: {
      // قيم من الصف
      rowOrder,
      rowEmail,
      rowStatus,
      row_number: row.row_number ?? null,

      // قيمة الويب هوك للمقارنة لاحقًا
      orderNumber,

      // الحد المتبقي من الشيت
      limit
    }
  });
}

return out;

const itemsIn = $input.all();
if (!itemsIn || itemsIn.length === 0) {
  return [{ json: { found: false, reason: "NOT_FOUND" } }];
}

// رجّع أول نتيجة أو كل النتائج حسب حاجتك
const first = itemsIn[0].json;

return [{
  json: {
    found: true,
    orderNumber: first.orderNumber,
    status: first.rowStatus,
    accountEmail: first.rowEmail,
    row_number: first.row_number ?? null,
    limit: first.limit ?? null // ← أضف هذا السطر لإظهار الـ limit
  }
}];

const code = $input.first()?.json?.token || $input.first()?.json?.code || null;

const now = Math.floor(Date.now() / 1000);
const timeStep = 30;
const secondsLeft = timeStep - (now % timeStep);

const codeExpired = secondsLeft <= 25;

return [{
  json: {
    code,
    codeExpired,
    secondsLeft
  }
}];
